.checkoutHeader-content {
    padding: 35px 0;
}

.checkout-steps {
    >.checkout-step {
        padding: 0;
        border: 0;

        +.checkout-step {
            padding-top: 10px;
        }
    }

    .stepHeader {
        padding: 0 0 30px 0;
        position: relative;
        flex-wrap: wrap;

        .stepHeader-body {
            margin: 0;

            .customerView {
                margin: 28px 0 0 0;
            }
        }

        .stepHeader-actions {
            position: absolute;
            top: 12px;
            right: 0;
            margin: 0;
            z-index: 10;

            .optimizedCheckout-buttonSecondary {
                border-radius: 0;
                background-color: transparent;
                border: none;
                padding: 0;
                height: auto;
                line-height: 26px;
                font-weight: normal;
                font-style: normal;
                box-shadow: none;
                color: stencilColor("optimizedCheckout-link-hoverColor");
                background-image: linear-gradient(transparent 97%, stencilColor("optimizedCheckout-link-hoverColor") 3%);
                background-repeat: repeat-x;
                background-position-y: -3px;
                margin: 0;
                text-align: right;
                min-width: unset;

                &:hover,
                &:focus {
                    box-shadow: none;
                }
            }
        }
    }

    .stepHeader-figure {
        display: block;
        width: 100%;
        height: auto;
        margin: 0;
        border-bottom: 2px solid stencilColor("optimizedCheckout-step-borderColor");
    }

    .checkout-view-content {
        .optimizedCheckout-form-checklist {
            border: 1px solid #e6e6e6;

            .optimizedCheckout-form-checklist-item {
                border-color: #e6e6e6;
                background: #ffffff;

                &.form-checklist-item--selected {
                    background: #fafafa;
                }
            }
        }
    }

    .checkout-step--payment {
        .form-label {
            font-weight: normal;
            color: stencilColor("optimizedCheckout-contentPrimary-color");
        }
    }

    @media (min-width: 768px) {
        .stepHeader {
            .stepHeader-body {
                margin-left: 74px;
            }
        }
    }
