.customerView[data-test="checkout-customer-info"] {
    flex-wrap: wrap;

    .customerView-actions {
        margin-top: 10px;
    }
}

.alertBox-message {

    strong,
    b {
        line-height: inherit;
        display: inline-block;
        vertical-align: middle;
    }
}

.checkout-view-header .stepHeader {
    display: inline-block;
    width: 100%;
    padding-top: 0;
    padding-bottom: 0;
    position: relative;

    .stepHeader-row {
        flex-wrap: wrap;
    }

    .stepHeader-figure {
        align-items: center;
        align-content: center;

        .stepHeader-counter {
            height: 48px;
            width: 48px;
            display: inline-block;

            &:before {
                width: 48px;
                height: 48px;
                line-height: 48px;
            }

            &.stepHeader-counter--complete {
                transform: scale(1);

                &:before {
                    width: 0;
                    height: 0;
                }
            }
        }
    }

    .stepHeader-body {
        margin-top: 0;

        >.customerView,
        >.staticConsignmentContainer,
        >.checkout-address--static {
            margin: 20px 0;
        }

        .optimizedCheckout-contentPrimary {
            line-height: 1.9;
            color: stencilColor("optimizedCheckout-headingSecondary-color");
        }

        .first-name,
        .family-name {
            color: stencilColor("optimizedCheckout-headingSecondary-color");
            font-weight: 700;
        }

        p {
            line-height: 1.9;
        }

        .shippingOption-desc,
        .shippingOption-price {
            font-size: stencilNumber('fontSize-root') + 2;
            line-height: 1.9;
        }
    }
}

.checkout-view-content {
    display: block;
    width: 100%;
    max-width: 605px;
    padding: 20px 0 25px 0;

    &.checkout-view-content-enter-done {
        padding: 30px 0 25px 0;
    }

    .checkout-form {
        margin: 0;
    }

    .customerEmail-container {
        margin: 0;
        padding: 0;

        +p {
            margin: 8px 0 0 0;
        }
    }

    .customerEmail-body {
        width: 100%;
    }

    .customerEmail-action {
        width: 100%;
        margin: 15px 0 0 0;
        padding: 0 0 0 0;
    }

    [data-test="forgot-password-link"] {
        display: inline-block;
        vertical-align: top;
        position: relative;
        margin: 13px 0 0 0;

        &:before {
            content: '';
            position: absolute;
            left: 0;
            bottom: 0;
            width: 100%;
            height: 1px;
            background: stencilColor("optimizedCheckout-link-color");
            transition: all ease .3s;
        }

        &:focus,
        &:hover {
            &:before {
                background: stencilColor("optimizedCheckout-link-hoverColor");
            }
        }
    }

    .optimizedCheckout-form-checklist {
        border: none;
        border-radius: 0;

        .form-checklist-checkbox~.form-label {
            padding-top: 12px;
            padding-bottom: 12px;
            min-height: 0;
        }

        .form-checklist-checkbox~.form-label::before,
        .form-checklist-checkbox~.form-label::after {
            width: 20px;
            height: 20px;
        }
    }

    .form-checklist-checkbox~.form-label {
        border: none;
        border-radius: 0;
        margin-bottom: 0;
    }

    #checkout-shipping-comments {
        .optimizedCheckout-form-input {
            min-height: 75px;
        }
    }

    .redeemable-payments {
        .redeemable-label {
            font-family: stencilFontFamily("optimizedCheckout-contentPrimary-font"), Arial, Helvetica, sans-serif;
            font-weight: bold;
            color: stencilColor("optimizedCheckout-headingPrimary-color");
            font-size: 16px;
            font-style: normal;
            line-height: 26px;
            margin: 0 0 8px 0;
        }

        .button {
            height: 50px;
            line-height: 50px;
            margin-left: 15px;
        }
    }

    @media (min-width: 551px) {
        .optimizedCheckout-form-checklist {

            .form-checklist-checkbox~.form-label {
                padding-left: 63px;
            }

            .form-checklist-checkbox~.form-label::before,
            .form-checklist-checkbox~.form-label::after {
                width: 26px;
                height: 26px;
            }
        }
    }

    @media (min-width: 768px) {
        padding-bottom: 45px;

        .checkout-form {
            margin-left: 74px;
        }

        .customerEmail-body {
            width: 55%;
        }

        .customerEmail-action {
            width: 45%;
            margin-top: 34px;
            padding-left: 15px;
        }
    }
}

