    <div class="productView-securityImage">
        <ul>
            {{#if theme_settings.halo-product-security-1}}
                <li>
                    <img src="{{cdn 'img/norton.png'}}" alt="Norton Secured">
                </li>
            {{/if}}
            {{#if theme_settings.halo-product-security-2}}
                <li>
                    <img src="{{cdn 'img/etrust.png'}}" alt="TRUSTe Verified">
                </li>
            {{/if}}
            {{#if theme_settings.halo-product-security-3}}
                <li>
                    <img src="{{cdn 'img/authorized-net.png'}}" alt="Authorized.net">
