pages/category:
---
category:
    shop_by_price: true
    products:
        limit: {{theme_settings.categorypage_products_per_page}}
products:
    featured:
        limit: {{theme_settings.sidebar_products_per_page}}
    new:
        limit: 25
    top_sellers:
        limit: {{theme_settings.sidebar_products_per_page}}
---
{{inject "categoryProductsPerPage" theme_settings.categorypage_products_per_page}}
{{inject "categoryId" category.id}}
{{#partial "head"}}
    {{#if pagination.category.previous}}
        <link rel="prev" href="{{pagination.category.previous}}">
    {{/if}}
    {{#if pagination.category.next}}
        <link rel="next" href="{{pagination.category.next}}">
    {{/if}}
{{/partial}}

{{#partial "page"}}

{{#if banners.top}}
    <div class="page-listing-header">
        {{{limit banners.top 1}}}
    </div>
{{/if}}

{{{region name="category_top_banner"}}}
{{{region name="category_top_banner--global"}}}

<div class="container">
    <div class="breadcrumb-wrapper">
        {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    </div>

    <div class="page page-category page-listing">
        <div class="page-header">
            <h1 class="page-heading">{{category.name}}</h1>
            {{#if category.description}}
                <div class="page-description">
                    {{{category.description}}}
                </div>
            {{/if}}
            {{{region name="category_below_header"}}}
        </div>

        {{#if theme_settings.halo-category-sidebar}}
            <aside class="page-sidebar" id="faceted-search-container">
                <div class="page-sidebar-close">
                    <a href="#" class="close">
                        <span class="icon">Ã—</span>
                        <span class="text">{{lang 'common.close'}}</span>
                    </a>
                </div>
                {{> components/category/sidebar}}
            </aside>
        {{/if}}

        <main class="page-content" id="product-listing-container">
            <div class="halo-toolbar">
                {{#if theme_settings.halo-category-sidebar}}
                    <div class="page-sidebar-mobile-wrapper">
                        <div class="page-sidebar-mobile">
                            <span class="text">{{lang 'common.sidebar'}}</span>
                            <svg class="icon" aria-hidden="true"><use xlink:href="#icon-sidebar"></use></svg>
                        </div>
                    </div>
                {{/if}}
                {{#if category.products}}
                    {{> components/halothemes/gallery/halo-toolbar sort=pagination.category.sort}}
                {{/if}}
            </div>
            {{#if category.products}}
                {{> components/category/product-listing productsnew=products.new show_compare=category.show_compare}}
            {{else}}
                <p class="page-no-products">{{lang 'categories.no_products'}}</p>
            {{/if}}
            {{{region name="category_below_content"}}}
        </main>
    </div>
</div>

<div class="page-listing-footer">
    {{{region name="category_bottom_banner"}}}
    {{{region name="category_bottom_banner--global"}}}
</div>

{{#if banners.bottom}}
    <div class="page-listing-footer">
        {{{limit banners.bottom 1}}}
    </div>
{{/if}}
{{/partial}}
{{> layout/base}}

components/halothemes/sidebar/all-catego:
{{#if theme_settings.halo-sidebar-all-categories}}
    <div class="sidebarBlock" id="allCategories">
        <h4 class="sidebarBlock-heading">Categories</h4>
        {{#if categories}}
            <ul class="all-categories-list">
                {{#each categories}}
                    <li class="navPages-item{{#if is_active}} current-cate is-clicked{{/if}}">
                        {{#if children}}
                            <a class="navPages-action has-subMenu" href="{{url}}">{{name}}</a>
                            <span class="icon-dropdown">
                                <i class="icon-plus"></i>
                            </span>
                            <ul class="dropdown-category-list navPages-level-2">
                                {{#each children}}
                                    <li class="navPage-subMenu-item{{#if is_active}} current-cate is-clicked{{/if}}">
                                        {{#if children}}
                                            <a class="navPage-subMenu-action navPages-action has-subMenu" href="{{url}}">
                                                {{name}}
                                            </a>
                                            <span class="icon-dropdown">
                                                <i class="icon-plus"></i>
                                            </span>
                                            <ul class="dropdown-category-list navPages-level-3">
                                                {{#each children}}
                                                    <li class="navPage-subMenu-item{{#if is_active}} current-cate is-clicked{{/if}}">
                                                        <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                                                    </li>
                                                {{/each}}
                                            </ul>
                                        {{else}}
                                            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                                        {{/if}}
                                    </li>
                                {{/each}}
                            </ul>
                        {{else}}
                            <a class="navPages-action" href="{{url}}">{{name}}</a>
                        {{/if}}
                    </li>
                {{/each}}
            </ul>
        {{/if}}
    </div>
{{/if}}

components/category/shop-by-price:
{{#and theme_settings.shop_by_price_visibility shop_by_price}}
    <div class="sidebarBlock">
        <h4 class="sidebarBlock-heading">{{lang 'category.shop_by_price'}}</h4>
        <ul class="navList">
            {{#each shop_by_price}}
            <li class="navList-item">
                <a class="navList-action {{#if selected }} is-active {{/if}}" href="{{url}}" alt="{{low.formatted}} - {{high.formatted}}" title="{{low.formatted}} - {{high.formatted}}"><span>{{low.formatted}} - {{high.formatted}}</span></a>
            </li>
            {{/each}}

            {{#any shop_by_price selected=true}}
                <li class="navList-item view-all">
                    <a href="{{category_url}}">
                        <span>{{lang 'category.reset'}}</span>
                    </a>
                </li>
            {{/any}}
        </ul>
    </div>
{{/and}}

