                                    <li><a href="{{theme_settings.footer-info-2-link5}}" class="link">{{theme_settings.footer-info-2-text5}}</a></li>
                                {{/if}}
                                {{#if theme_settings.footer-info-2-text6}}
                                    <li><a href="{{theme_settings.footer-info-2-link6}}" class="link">{{theme_settings.footer-info-2-text6}}</a></li>
