components/products/reviews:
{{#if reviews.total}}
    <div class="productReview-total"><span>{{reviews.total}}{{#if reviews.tota '>' l}} Reviews{{else}} Review{{/if}}</span></div>
    <ul class="productReviews-list" id="productReviews-list">
        {{#each reviews.list}}
            <li class="productReview">
                <article>
                    <header>
                        <h3 class="productReview-title">
                            <span class="productReview-title-text">{{{ sanitize title }}}</span>
                            <span class="productReview-rating rating--small">
                                {{> components/products/ratings rating=rating}}
                                <span class="productReview-ratingNumber">{{ rating }}</span>
                            </span>
                        </h3>
                        {{#if name}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=name }}
                            </p>
                        {{else}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                        {{/if}}
                    </header>
                    <p class="productReview-body">{{{ sanitize text }}}</p>
                </article>
            </li>
        {{/each}}
    </ul>
    {{#or pagination.reviews.next pagination.reviews.previous}}
        {{> components/common/paginator pagination.reviews}}
    {{/or}}
    <div class="button-createReview">
        <a class="button button--primary" href="#" data-reveal-id="modal-review-form">{{lang 'products.reviews.write_a_review'}}</a>
    </div>
{{else}}
    <div class="alertBox alertBox--warning">
        <div class="alertBox-column alertBox-icon">
            <span class="icon" aria-hidden="true"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"></path></svg></span>
        </div>
        <p class="alertBox-column alertBox-message">
            {{lang 'products.reviews.write_a_review_text'}}
        </p>
    </div>
    <div class="button-createReview">
        <a class="button button--primary" href="#" data-reveal-id="modal-review-form">{{lang 'products.reviews.write_a_review'}}</a>
    </div>
{{/if}}
